SELECT bus_schedule_id, sour_id, dist_id, num, price, date, time, distance, lasting, type
FROM t_bus_schedule
WHERE date = #{date} AND sour_id = #{sourId} AND dist_id = #{distId}

SELECT bus_schedule_id, sour_id, dist_id, num, price, date, time, distance, lasting, type
FROM t_bus_schedule
WHERE date = #{date} AND time = #{time} AND sour_id = #{sourId} AND dist_id = #{distId}

SELECT bus_schedule_id, sour_id, dist_id, num, price, date, time, distance, lasting, type
FROM t_bus_schedule
WHERE date = #{date}
