mapper.select:
SELECT ticket_id, sour_id, dist_id, num, price, date, time, distance, lasting, balance
FROM t_ticket
WHERE ticket_id = #{ticketId}

SELECT ticket_id, sour_id, dist_id, num, price, date, time, distance, lasting, balance
FROM t_ticket
WHERE date = #{date}

SELECT ticket_id, sour_id, dist_id, num, price, date, time, distance, lasting, balance
FROM t_ticket
WHERE sour_id = #{sourId} AND dist_id = #{distId}

SELECT ticket_id, sour_id, dist_id, num, price, date, time, distance, lasting, balance
FROM t_ticket
WHERE date = #{date} AND sour_id = #{sourId} AND dist_id = #{distId}

mapper.update:
UPDATE t_ticket SET
    balance = balance - #{count}
WHERE ticket_id = #{ticketId}
