mapper.select:
SELECT ticket_id, path_id, num, price, date, time, balance
FROM t_ticket
WHERE ticket_id = #{ticketId}

SELECT ticket_id, path_id, num, price, date, time, balance
FROM t_ticket
WHERE date = #{date}

mapper.update:
UPDATE t_ticket SET
    balance = balance - #{count}
WHERE ticket_id = #{ticketId}
