mapper.select:
SELECT ticket_id, path_id, num, price, date, time
FROM t_ticket
WHERE date = #{date}

SELECT ticket_id, path_id, num, price, date, time
FROM t_ticket
WHERE path_id = #{pathId}

SELECT ticket_id, path_id, num, price, date, time
FROM t_ticket
WHERE path_id = #{pathId} AND date = #{date}
